		 */
		List<Integer> toRemoveList = new ArrayList<>();
		for (int i = 0; i < list.size(); i++) {
			int value = list.get(i);
			if(value == toRemoveValue) {
				toRemoveList.add(value);
			}
		}

		list.removeAll(toRemoveList);

		//----------------------------------------------------------------------------
		/**
